public class ServiceRegistry : IServiceRegistry\n{\n    private Dictionary<string, string> services = new Dictionary<string, string>();\n\n    public void RegisterService(string serviceName, string endpointAddress)\n    {\n        if (!services.ContainsKey(serviceName))\n            services[serviceName] = endpointAddress;\n    }\n\n    public string ResolveService(string serviceName)\n    {\n        return services.ContainsKey(serviceName) ? services[serviceName] : null;\n    }\n}