using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic class DoubleClickTabControl : TabControl\n{\n    public event EventHandler<MouseEventArgs> TabDoubleClicked;\n\n    protected override void OnDoubleClick(MouseEventArgs e)\n    {\n        base.OnDoubleClick(e);\n\n        // Find which tab was double-clicked.\n        for (int i = 0; i < this.TabCount; i++)\n        {\n            Rectangle r = GetTabRect(i);\n            \n            if (r.Contains(e.Location))\n            {\n                TabDoubleClicked?.Invoke(this, new MouseEventArgs(MouseButtons.Left, 2, e.X, e.Y, e.Delta));\n                break;\n            }\n        }\n    }\n}\n\n// In your form code\npublic class MyForm : Form\n{\n    public MyForm()\n    {\n        DoubleClickTabControl tabControl = new DoubleClickTabControl();\n        tabControl.Dock = DockStyle.Fill;\n        tabControl.TabDoubleClicked += Tab_DoubleClicked;\n\n        // Add some tabs for demonstration\n        tabControl.TabPages.Add("Tab 1");\n        tabControl.TabPages.Add("Tab 2");\n\n        this.Controls.Add(tabControl);\n    }\n\n    private void Tab_DoubleClicked(object sender, MouseEventArgs e)\n    {\n        MessageBox.Show("Double-clicked on a tab!");\n    }\n}