class Program\n{\n    static void Main(string[] args)\n    {\n        Uri baseAddress = new Uri("http://localhost:8000/AccessDatabaseWrapper");\n        \n        using (ServiceHost host = new ServiceHost(typeof(AccessDatabaseWrapper), baseAddress))\n        {\n            ServiceMetadataBehavior smb = new ServiceMetadataBehavior();\n            smb.HttpGetEnabled = true;\n            host.Description.Behaviors.Add(smb);\n\n            BasicHttpBinding binding = new BasicHttpBinding();\n            host.AddServiceEndpoint(typeof(IAccessDatabaseWrapper), binding, "");\n\n            try\n            {\n                host.Open();\n                Console.WriteLine("The service is ready.");\n                Console.ReadLine(); \n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine(ex.Message);\n            }\n        }            \n    }\n}