protected void LazyLoadUpdatePanel_Tick(object sender, EventArgs e)\n{\n    if (Session["DataSource"] == null)\n    {\n        System.Threading.Thread.Sleep(2000);\n        // Assuming customer.Orders needs to be converted to something enumerable like List<T>\n        Session["DataSource"] = customer.Orders.ToList();\n    }\n\n    var dataSource = (List<Order>)Session["DataSource"]; // Change Order to your actual data type\n\n    OrdersGridView.PageIndex = OrdersGridView.PageIndex; // Ensure you maintain the current page index\n    OrdersGridView.DataSource = dataSource;\n    OrdersGridView.DataBind();\n\n    OrdersUpdatePanel.Update();\n\n    LazyLoadUpdatePanel.Enabled = false;\n}