protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        string orderId = Request.QueryString["SelectOrderId"];\n        if (!string.IsNullOrEmpty(orderId))\n        {\n            HighlightRowInDataGrid(orderId);\n        }\n    }\n}\n\nprivate void HighlightRowInDataGrid(string orderId)\n{\n    foreach (GridViewRow row in DataGrid1.Rows)\n    {\n        // Assuming OrderId is in the first column\n        string currentOrderId = DataGrid1.DataKeys[row.RowIndex].Value.ToString();\n        if (currentOrderId == orderId)\n        {\n            row.Focus(); // Focus on the row\n            break;\n        }\n    }\n}