using System.Windows.Forms;\n\npublic void OpenAssociationSettings()\n{\n    var openFileDialog = new OpenFileDialog();\n    openFileDialog.Title = "Associate .lala files";\n    openFileDialog.Filter = "Executable Files (*.exe)|*.exe";\n\n    if (openFileDialog.ShowDialog() == DialogResult.OK)\n    {\n        string applicationPath = openFileDialog.FileName;\n        AssociateFileType(".lala", "YourApp.lala", "Lala File", applicationPath);\n    }\n}