using System;\nusing System.Xml.XPath;\n\npublic class XPathValidator\n{\n    public static bool IsValidXPath(string xpath)\n    {\n        try\n        {\n            // Try to compile the XPath expression\n            XPathExpression.Compile(xpath);\n            return true; // If compilation succeeds, the XPath is valid\n        }\n        catch (XPathException) \n        {\n            // If an exception occurs, the XPath is invalid\n            return false;\n        }\n    }\n\n    public static void Main()\n    {\n        string testXPath = "//element[@attribute='value']";\n        Console.WriteLine($"Is the XPath '{testXPath}' valid? {IsValidXPath(testXPath)}");\n\n        testXPath = "//invalid:expression]]]";\n        Console.WriteLine($"Is the XPath '{testXPath}' valid? {IsValidXPath(testXPath)}");\n    }\n}