// Assuming ExternalModel comes from a third-party library or WCF service\npublic class ExternalModel\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n}\n\n// Adapted ViewModel specifically for the view\npublic class MyViewModel\n{\n    public int ModelId { get; set; }\n    public string ModelName { get; set; }\n\n    // Constructor to map ExternalModel to MyViewModel\n    public MyViewModel(ExternalModel externalModel)\n    {\n        if (externalModel != null)\n        {\n            ModelId = externalModel.Id;\n            ModelName = externalModel.Name;\n        }\n    }\n}