public class YourHandler : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        var id = context.Request.Form["id"]; // Retrieve the 'id' sent via POST\n        if (!string.IsNullOrEmpty(id))\n        {\n            // Perform your server-side operation here using the retrieved ID\n\n            context.Response.ContentType = "application/json";\n            context.Response.Write("{\"status\":\"success\"}");\n        }\n        else\n        {\n            context.Response.StatusCode = 400; // Bad Request\n            context.Response.Write("{\"error\":\"Invalid request\"}");\n        }\n    }\n\n    public bool IsReusable => false;\n}