using System;\nusing System.Data;\nusing System.IO;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        DataSet ds = new DataSet();\n        string xsdPath = "path/to/schema.xsd";\n        string xmlPath = "path/to/data.xml";\n\n        // Load the schema first\n        ds.ReadXmlSchema(xsdPath);\n\n        // Use XmlReader for efficient XML processing\n        using (XmlReader reader = XmlReader.Create(xmlPath))\n        {\n            foreach (DataTable dt in ds.Tables)\n            {\n                dt.BeginLoadData();\n            }\n            \n            ds.ReadXml(reader);\n            \n            foreach (DataTable dt in ds.Tables)\n            {\n                dt.EndLoadData();\n            }\n        }\n\n        Console.WriteLine("DataSet loaded successfully with minimized constraints.");\n    }\n}