using System;\nusing System.Linq;\nusing Oracle.ManagedDataAccess.Client; // Ensure you have this using directive for ODP.NET\nusing Oracle.ManagedDataAccess.EntityFramework; // For EF support\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "User Id=yourUsername; Password=yourPassword; Data Source=YourDataSource;";\n        \n        using (OracleConnection connection = new OracleConnection(connectionString))\n        {\n            connection.Open();\n            \n            // Assuming you have an Entities class generated by EF for your schema\n            using (var context = new YourEntities(connection, true))\n            {\n                var query = from t in context.YourTable\n                            where t.SomeColumn == "somevalue"\n                            select t;\n                \n                foreach (var item in query)\n                {\n                    Console.WriteLine(item.SomeOtherColumn);\n                }\n            }\n        }\n    }\n}