using Gettext;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        // Initialize a new catalog with the specified language and domain.\n        var catalog = new Catalog("fr-FR", "messages");\n\n        // Add translations to the catalog.\n        catalog.Add(new Entry("Hello, World!", "Bonjour le Monde!"));\n\n        // Save as .po file\n        using (var poStream = File.CreateText("path/to/your/messages.po"))\n        {\n            catalog.Save(poStream);\n        }\n\n        // Save as .mo file\n        using (var moStream = File.Create("path/to/your/messages.mo"))\n        {\n            catalog.WriteBinary(moStream);\n        }\n    }\n}