using System;\n   using System.Security.Principal;\n   using System.Linq;\n   using System.DirectoryServices.AccountManagement;\n\n   // ...\n\n   foreach (string group in rowGroups)\n   {\n       var contextType = ContextType.Machine; // or Domain if specific domain needed\n\n       using (var principalContext = new PrincipalContext(contextType))\n       {\n           using (UserPrincipal userPrincipal = UserPrincipal.Current)\n           {\n               found = userPrincipal.IsMemberOf(new GroupPrincipal(principalContext, group));\n               if (found)\n                   break;\n           }\n       }\n   }