using System;\n\nclass Program\n{\n    static void Main()\n    {\n        Guid guid = Guid.NewGuid();\n        byte[] bytes = guid.ToByteArray();\n\n        // Convert the first 4 bytes of the array to an integer (which corresponds to Data1 in C++)\n        int data1 = BitConverter.ToInt32(bytes, 0);\n\n        // Perform bitwise AND operation\n        int dwRand = data1 & 0x7FFFFFFF;\n\n        Console.WriteLine("Guid: " + guid);\n        Console.WriteLine("Data1: " + data1);\n        Console.WriteLine("dwRand: " + dwRand);\n    }\n}