using System;\nusing System.Web.Profile;\n\npublic void SetUserProfile(string username, Guid userGuid, string stringProp1, string stringProp2, int integerProp)\n{\n    // Get the profile of an existing user by their username\n    ProfileBase userProfile = ProfileBase.Create(username);\n\n    // Set the properties\n    userProfile.SetPropertyValue("UserGuid", userGuid);\n    userProfile.SetPropertyValue("StringProperty1", stringProp1);\n    userProfile.SetPropertyValue("StringProperty2", stringProp2);\n    userProfile.SetPropertyValue("IntegerProperty", integerProp);\n\n    // Save the changes to the profile\n    userProfile.Save();\n}