[ScriptMethod(ResponseFormat = ResponseFormat.Json)]\n[WebMethod]\npublic string MyWebMethod(string input)\n{\n    if (!ValidInput(input))\n    {\n        string l_errMsg = HttpUtility.HtmlEncode(GetErrorMessage());\n        throw new WebServiceException(l_errMsg); // Use a custom exception if needed\n    }\n\n    // some work gets done...\n\n    return HttpUtility.HtmlEncode(l_returnVal);\n}\n\n// Custom Exception to ensure UTF-8 encoding in the error message\npublic class WebServiceException : Exception\n{\n    public WebServiceException(string message) : base(message)\n    {\n        HttpContext.Current.Response.ContentType = "application/json; charset=utf-8";\n    }\n}