using System;\n\npublic class GuidConverter\n{\n    // Convert GUID to byte array (for BINARY(16) storage)\n    public static byte[] GuidToByteArray(Guid guid)\n    {\n        return guid.ToByteArray();\n    }\n\n    // Convert byte array back to GUID from BINARY(16) storage\n    public static Guid ByteArrayToGuid(byte[] byteArray)\n    {\n        if (byteArray.Length != 16)\n            throw new ArgumentException("Byte array must have 16 bytes");\n\n        return new Guid(byteArray);\n    }\n}\n\n// Example usage:\npublic class Program\n{\n    public static void Main()\n    {\n        Guid exampleGuid = Guid.NewGuid();\n        \n        // Convert and store as BINARY(16)\n        byte[] guidBytes = GuidConverter.GuidToByteArray(exampleGuid);\n        \n        // Retrieve and convert back to GUID\n        Guid retrievedGuid = GuidConverter.ByteArrayToGuid(guidBytes);\n        \n        Console.WriteLine($"Original: {exampleGuid}");\n        Console.WriteLine($"Converted back: {retrievedGuid}");\n    }\n}