using System.Data;\n\n// Assuming 'dataTable' is your DataTable instance\n\n// Set Col A and Col B as a compound primary key\nDataColumn[] keys = new DataColumn[2];\nkeys[0] = dataTable.Columns["Col A"];\nkeys[1] = dataTable.Columns["Col B"];\ndataTable.PrimaryKey = keys;\n\n// Values to find in the compound key\nobject[] searchValues = new object[2];\nsearchValues[0] = 6; // Value for Col A\nsearchValues[1] = 5; // Value for Col B\n\n// Use Find method with the array of search values\nDataRow foundRow = dataTable.Rows.Find(searchValues);\n\nif (foundRow != null)\n{\n    // Row found, you can work with 'foundRow' here\n    Console.WriteLine("Found row: " + foundRow["Col A"] + ", " + foundRow["Col B"]);\n}\nelse\n{\n    // Row not found\n    Console.WriteLine("No row found.");\n}