using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class MySqlPasswordHasher\n{\n    public static string HashPassword(string input)\n    {\n        // First hash using SHA1\n        byte[] firstHash = ComputeSha1Hash(input);\n        // Second hash on the result of the first hash is also SHA1-based but transformed into a hexadecimal format\n        byte[] secondHash = ComputeSha1Hash(BitConverter.ToString(firstHash).ToLower().Replace("-", ""));\n\n        StringBuilder builder = new StringBuilder();\n        foreach (byte b in secondHash)\n        {\n            builder.AppendFormat("{0:x2}", b);\n        }\n        return "*"+builder.ToString();\n    }\n\n    private static byte[] ComputeSha1Hash(string rawData)\n    {\n        using (SHA1 sha1Algorithm = SHA1.Create())\n        {\n            byte[] dataBuffer = Encoding.UTF8.GetBytes(rawData);\n            return sha1Algorithm.ComputeHash(dataBuffer);\n        }\n    }\n\n    public static void Main()\n    {\n        string password = "yourPassword";\n        Console.WriteLine("MySQL hashed password: {0}", HashPassword(password));\n    }\n}