using System;\nusing System.IO;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        string path = @"c:\windows\system32";\n        string searchPattern = "*.dll";\n\n        int ComponentCount = GetFileCount(path, searchPattern);\n\n        Console.WriteLine($"Number of files with the extension '{searchPattern}': {ComponentCount}");\n    }\n\n    static int GetFileCount(string path, string searchPattern)\n    {\n        try\n        {\n            // Use SearchOption.AllDirectories to include all subdirectories\n            return Directory.GetFiles(path, searchPattern, SearchOption.AllDirectories).Length;\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n            return 0;\n        }\n    }\n}