using System;\nusing System.Runtime.InteropServices;\n\nclass Program {\n    [DllImport("YourCppDll.dll", CallingConvention = CallingConvention.Cdecl)]\n    private static extern IntPtr CreateMyClass(int initialData);\n\n    [DllImport("YourCppDll.dll", CallingConvention = CallingConvention.Cdecl)]\n    private static extern void DestroyMyClass(IntPtr obj);\n\n    [DllImport("YourCppDll.dll", CallingConvention = CallingConvention.Cdecl)]\n    private static extern void IncreaseData(IntPtr obj);\n\n    [DllImport("YourCppDll.dll", CallingConvention = CallingConvention.Cdecl)]\n    private static extern int GetData(IntPtr obj);\n\n    static void Main() {\n        IntPtr myCppObject = CreateMyClass(5);\n        Console.WriteLine(GetData(myCppObject)); // Outputs 5\n        IncreaseData(myCppObject);\n        Console.WriteLine(GetData(myCppObject)); // Outputs 6\n        DestroyMyClass(myCppObject);\n    }\n}