using System;\nusing System.Data.SqlClient;\n\npublic void AppendBlobData(string connectionString, int fileId, byte[] newData)\n{\n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        connection.Open();\n\n        // First, get the current length of the BLOB data.\n        SqlCommand cmdLength = new SqlCommand("SELECT DATALENGTH(blobColumn) FROM YourTable WHERE id = @fileId", connection);\n        cmdLength.Parameters.AddWithValue("@fileId", fileId);\n\n        int currentLength = (int)(long)cmdLength.ExecuteScalar();\n\n        // Use the WRITE clause to append data\n        SqlCommand cmdWrite = new SqlCommand(\n            "UPDATE YourTable SET blobColumn.WRITE(@newData, NULL, 0) WHERE id = @fileId",\n            connection);\n        \n        cmdWrite.Parameters.AddWithValue("@newData", newData);\n        cmdWrite.Parameters.AddWithValue("@fileId", fileId);\n\n        // If you want to append at the end of the current data\n        // Use DATALENGTH(blobColumn) as the offset for writing\n        cmdWrite.CommandText = $"UPDATE YourTable SET blobColumn.WRITE(@newData, {currentLength}, 0) WHERE id = @fileId";\n\n        int rowsAffected = cmdWrite.ExecuteNonQuery();\n    }\n}