using (var context = new YourDataContext())\n{\n    var query =\n        "select distinct top 1 'PostId' = isnull(RootPost,Id), PostedDateTimeUtc from Post order by PostedDateTimeUtc desc";\n\n    var result = context.ExecuteQuery<dynamic>(\n        query)\n        .Select(x => new { PostId = (int)x.PostId, PostedDateTimeUtc = (DateTime)x.PostedDateTimeUtc })\n        .FirstOrDefault();\n\n    if (result != null)\n    {\n        Console.WriteLine($"PostId: {result.PostId}, PostedDateTimeUtc: {result.PostedDateTimeUtc}");\n    }\n}