using System;\nusing System.Collections.Generic;\n\npublic class WordGenerator\n{\n    public static IEnumerable<string> GetWords(int length)\n    {\n        if (length <= 0) yield break;\n\n        char[] chars = new char[length];\n        int[] indices = new int[length];\n\n        while (true)\n        {\n            // Yield the current word\n            for (int i = 0; i < length; i++)\n                chars[i] = (char)('A' + indices[i]);\n            \n            yield return new string(chars);\n\n            // Find the rightmost index to increment\n            int position = length - 1;\n            while (position >= 0 && indices[position] == 25)\n            {\n                indices[position] = 0;\n                position--;\n            }\n\n            if (position < 0)\n                break; // All combinations have been generated\n\n            indices[position]++;\n        }\n    }\n\n    static void Main()\n    {\n        foreach (var word in GetWords(3))\n        {\n            Console.WriteLine(word);\n        }\n    }\n}