using (var context = new YourDbContext())\n{\n    var nextExample = (from e in context.Example\n                     where e.ExampleID == yourExampleId\n                     let next = (from ex in context.Example\n                                 where ex.TopicID == e.TopicID && ex.ExampleOrder > e.ExampleOrder\n                                 orderby ex.ExampleOrder\n                                 select ex)\n                     from n in next.Take(1).DefaultIfEmpty()\n                     select n).FirstOrDefault();\n}