using System;\nusing System.Windows.Forms;\nusing WeifenLuo.WinFormsUI.Docking;\n\npublic class MainForm : Form {\n    private DockPanel dockPanel;\n\n    public MainForm() {\n        InitializeComponent();\n    }\n\n    private void InitializeComponent() {\n        this.dockPanel = new DockPanel();\n        ((System.ComponentModel.ISupportInitialize)(this.dockPanel)).BeginInit();\n        this.SuspendLayout();\n\n        // \n        // dockPanel\n        // \n        this.dockPanel.Dock = DockStyle.Fill;\n        this.dockPanel.DocumentStyle = DocumentStyle.TabbedFloatingWindow;\n        this.dockPanel.Location = new System.Drawing.Point(0, 0);\n        this.dockPanel.Name = "dockPanel";\n        this.dockPanel.Size = new System.Drawing.Size(800, 450);\n        this.dockPanel.TabIndex = 0;\n\n        // \n        // MainForm\n        // \n        this.ClientSize = new System.Drawing.Size(800, 450);\n        this.Controls.Add(this.dockPanel);\n        this.Name = "MainForm";\n        ((System.ComponentModel.ISupportInitialize)(this.dockPanel)).EndInit();\n        this.ResumeLayout(false);\n    }\n\n    private void LoadDockableForms() {\n        var form1 = new DockableForm { Text = "Form 1" };\n        form1.Show(dockPanel, DockState.DockLeft);\n\n        var form2 = new DockableForm { Text = "Form 2" };\n        form2.Show(dockPanel, DockState.DockRight);\n    }\n\n    [STAThread]\n    static void Main(string[] args) {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n\n        MainForm mainForm = new MainForm();\n        mainForm.Load += (s, e) => { mainForm.LoadDockableForms(); };\n        Application.Run(mainForm);\n    }\n}\n\npublic class DockableForm : DockContent { }