using System;\nusing System.Messaging;\n\npublic class MessageQueueReader\n{\n    private const string QueuePath = @".\private$\YourQueueName";\n\n    public void ReadMessage()\n    {\n        using (var messageQueue = new MessageQueue(QueuePath))\n        {\n            // Set formatter for MSMQ messages\n            messageQueue.Formatter = new XmlMessageFormatter(new Type[] { typeof(string) });\n\n            // Try to receive a message\n            try\n            {\n                var message = messageQueue.Receive();\n                Console.WriteLine("Received: " + message.Body);\n            }\n            catch (MessageQueueException e)\n            {\n                Console.WriteLine($"Error receiving message: {e.Message}");\n            }\n        }\n    }\n}