using System.Collections.Generic;\nusing System.IO;\nusing DevExpress.Pdf;\n\npublic void CombinePdfs(List<string> pdfPaths, string outputPath)\n{\n    using (PdfDocumentProcessor processor = new PdfDocumentProcessor())\n    {\n        foreach (string pdfPath in pdfPaths)\n        {\n            if (File.Exists(pdfPath))\n            {\n                // Load and append each PDF file to the output document\n                using (FileStream sourceStream = File.OpenRead(pdfPath))\n                {\n                    processor.AppendDocument(sourceStream);\n                }\n            }\n        }\n\n        // Save the combined document to disk\n        using (FileStream outputStream = new FileStream(outputPath, FileMode.Create))\n        {\n            processor.SaveDocument(outputStream);\n        }\n    }\n}\n\n// Usage example after your foreach loop:\nList<string> pdfPaths = new List<string>();\nforeach (ReportClass report in reports)\n{\n    string filePath = @"c:\reports\" + report.ResourceName + ".pdf";\n    report.ExportToDisk(ExportFormatType.PortableDocFormat, filePath);\n    pdfPaths.Add(filePath);\n}\n\nstring combinedFilePath = @"c:\reports\CombinedWeeklyReports.pdf";\nCombinePdfs(pdfPaths, combinedFilePath);\n\n// Optionally, clean up individual PDF files\nforeach (var path in pdfPaths)\n{\n    File.Delete(path);\n}