public class BaseClass {\n    public void Display() {\n        Console.WriteLine("Display from BaseClass");\n    }\n}\n\npublic class DerivedClass : BaseClass {\n    public new void Display() {\n        Console.WriteLine("Display from DerivedClass");\n    }\n}\n\n// Usage\nBaseClass baseRef = new DerivedClass();\nbaseRef.Display(); // Outputs: Display from BaseClass\n\nDerivedClass derivedRef = new DerivedClass();\nderivedRef.Display(); // Outputs: Display from DerivedClass