public void EnableAppOffline()\n{\n    string path = Path.Combine(HttpRuntime.AppDomainAppPath, "app_offline.htm");\n    using (FileStream fs = new FileStream(path, FileMode.Create, FileAccess.Write))\n    {\n        using (StreamWriter sw = new StreamWriter(fs))\n        {\n            sw.WriteLine("Application is down for maintenance.");\n        }\n    }\n}\n\npublic void DisableAppOffline()\n{\n    string path = Path.Combine(HttpRuntime.AppDomainAppPath, "app_offline.htm");\n    if (File.Exists(path))\n    {\n        File.Delete(path);\n    }\n}