using System;\nusing System.Threading;\n\nclass Program\n{\n    static void Main()\n    {\n        // Define your column headers and width\n        string[] headers = { "Column 1", "Column 2", "Column 3", "Column 4" };\n        int[] widths = { 20, 20, 20, 20 };\n\n        // Draw the table header\n        DrawTableHeader(headers, widths);\n\n        Console.WriteLine(new string('-', (widths[0] + 3) * headers.Length));\n\n        while (true)\n        {\n            // Simulate data change every second\n            var data = new[]\n            {\n                Guid.NewGuid().ToString("N").Substring(0, 17),\n                DateTime.Now.ToString(),\n                RandomNumber(),\n                "SomeData"\n            };\n\n            DrawTableRow(data, widths);\n\n            // Refresh table fast by clearing the console\n            Console.SetCursorPosition(0, headers.Length + 2);\n\n            Thread.Sleep(1000); // Wait for 1 second\n        }\n    }\n\n    static void DrawTableHeader(string[] headers, int[] widths)\n    {\n        Console.Write("|");\n        for (int i = 0; i < headers.Length; i++)\n        {\n            Console.Write($" {headers[i].PadRight(widths[i])} |");\n        }\n        Console.WriteLine();\n        Console.WriteLine(new string('-', (widths[0] + 3) * headers.Length));\n    }\n\n    static void DrawTableRow(string[] data, int[] widths)\n    {\n        Console.Write("|");\n        for (int i = 0; i < data.Length; i++)\n        {\n            Console.Write($" {data[i].PadRight(widths[i])} |");\n        }\n        Console.WriteLine();\n    }\n\n    static string RandomNumber()\n    {\n        var random = new Random();\n        return random.Next(100, 1000).ToString();\n    }\n}