using System;\nusing System.Net.Sockets;\nusing System.Text;\nusing System.Threading.Tasks;\n\nclass Client {\n    static async Task Main(string[] args) {\n        string serverIp = "127.0.0.1"; // Change to your server's IP address\n        int port = 12345; // Change to your server's port\n\n        using TcpClient client = new TcpClient(serverIp, port);\n        Console.WriteLine("Connected to server.");\n\n        using var stream = client.GetStream();\n\n        byte[] dataToSend = Encoding.ASCII.GetBytes("Hello Server");\n        await stream.WriteAsync(dataToSend, 0, dataToSend.Length);\n\n        Console.WriteLine("Sent to server: " + "Hello Server");\n\n        byte[] buffer = new byte[1024];\n        int bytesRead = await stream.ReadAsync(buffer, 0, buffer.Length);\n        string responseFromServer = Encoding.ASCII.GetString(buffer, 0, bytesRead);\n\n        Console.WriteLine("Received from server: " + responseFromServer);\n    }\n}