using System;\nusing System.Net;\nusing System.Net.Sockets;\nusing System.Text;\nusing System.Threading.Tasks;\n\nclass DelayServer {\n    static async Task Main(string[] args) {\n        int port = 12345; // Change to your server's port\n\n        TcpListener listener = new TcpListener(IPAddress.Any, port);\n        listener.Start();\n        Console.WriteLine("Listening for client...");\n\n        while (true) {\n            using TcpClient client = await listener.AcceptTcpClientAsync();\n            Console.WriteLine("Client connected.");\n\n            using var stream = client.GetStream();\n\n            byte[] buffer = new byte[1024];\n            int bytesRead = await stream.ReadAsync(buffer, 0, buffer.Length);\n\n            string dataReceived = Encoding.ASCII.GetString(buffer, 0, bytesRead);\n            Console.WriteLine("Received from client: " + dataReceived);\n\n            // Simulate delay\n            await Task.Delay(5000); // Delay of 5 seconds\n\n            byte[] response = Encoding.ASCII.GetBytes("Delayed response from server");\n            await stream.WriteAsync(response, 0, response.Length);\n\n            Console.WriteLine("Sent delayed response.");\n        }\n    }\n}