private DataSet _personInfo;\n\nprivate void FormPaint(int personID)\n{\n    // Fetch data from your BLL and assign it to the form-level variable.\n    _personInfo = ConnectBLL.BLL.Person.GetPerson(personID);\n\n    if (_personInfo != null && _personInfo.Tables.Count > 0 && _personInfo.Tables[0].Rows.Count > 0)\n    {\n        // Update UI fields using data from dataset\n        DataRow row = _personInfo.Tables[0].Rows[0];\n        txtFirstName.Text = row.Field<string>("FirstName");\n        txtLastName.Text = row.Field<string>("LastName");\n        // and so on for other fields\n    }\n}\n\nprivate void btnUpdate_Click(object sender, EventArgs e)\n{\n    if (_personInfo.HasChanges())\n    {\n        DataSet changes = _personInfo.GetChanges();\n        \n        string changeDetails = "";\n        foreach (DataRow changeRow in changes.Tables[0].Rows)\n        {\n            changeDetails += $"ID: {changeRow.Field<int>("PersonID")},";\n            changeDetails += $"First Name: {changeRow.Field<string>("FirstName")},";\n            // Add other fields as necessary\n        }\n\n        var confirmChanges = MessageBox.Show(\n            "Are you sure you want to make these changes:\n" + changeDetails,\n            "Confirm Changes",\n            MessageBoxButtons.YesNo, MessageBoxIcon.Hand);\n\n        if (confirmChanges == DialogResult.Yes)\n        {\n            ConnectBLL.BLL.Person.Update(_personInfo);\n            _personInfo.AcceptChanges(); // Accept the changes once data is successfully updated.\n            \n            MessageBox.Show("Changes successfully saved.", "Success", MessageBoxButtons.OK, MessageBoxIcon.Information);\n        }\n        else\n        {\n            _personInfo.RejectChanges(); // Reject changes if user cancels update.\n        }\n    }\n    else\n    {\n        MessageBox.Show("No changes to save.", "Information", MessageBoxButtons.OK, MessageBoxIcon.Information);\n    }\n\n    FormPaint(HUD.PersonId); // Repaint the form with current data.\n}