public class RouteConfig\n   {\n       public static void RegisterRoutes(RouteCollection routes)\n       {\n           routes.IgnoreRoute("{resource}.axd/{*pathInfo}");\n\n           // Define specific routes for your generic controllers\n           routes.MapRoute(\n               name: "MyControllerForSpecificType",\n               url: "My/SpecificType/{action}/{id}",\n               defaults: new { controller = "MySpecificType", action = "Index", id = UrlParameter.Optional }\n           );\n\n           // Example of using a constraint for more generic routing\n           routes.MapRoute(\n               name: "Default",\n               url: "{controller}/{action}/{id}",\n               defaults: new { action = "Index", id = UrlParameter.Optional },\n               constraints: new { controller = @"My[a-zA-Z0-9]+" }\n           );\n       }\n   }