using System.Web.Mvc;\nusing System.Web.Routing;\n\npublic static class ButtonExtensions\n{\n    public static MvcHtmlString NavigateButton<TController>(\n        this HtmlHelper htmlHelper,\n        string buttonText,\n        Expression<Action<TController>> action)\n        where TController : Controller\n    {\n        var urlHelper = new UrlHelper(htmlHelper.ViewContext.RequestContext, htmlHelper.RouteCollection);\n        var routeValues = Microsoft.Web.Mvc.Internal.ExpressionHelper.GetRouteValuesFromExpression(action);\n\n        // Generate URL to the action specified in the expression\n        var formActionUrl = urlHelper.Action(routeValues["action"].ToString(), routeValues["controller"].ToString());\n\n        // Create a form that submits to the generated URL on button click\n        var formTag = string.Format("<form action=\"{0}\" method=\"post\" style=\"display:inline;\">\n" +\n                                    "<button type=\"submit\">{1}</button>\n" +\n                                    "</form>", formActionUrl, buttonText);\n\n        return MvcHtmlString.Create(formTag);\n    }\n}