public class GenericProxy<T>\n    where T : ClientBase\n{\n    private readonly T _proxy;\n\n    public GenericProxy(IClientFactory<T> factory, string configName)\n    {\n        _proxy = factory.CreateInstance(configName);\n    }\n}\n\n// Example implementation for a specific type:\npublic class ConcreteClientFactory : IClientFactory<ConcreteClient>\n{\n    public ConcreteClient CreateInstance(string configName)\n    {\n        return new ConcreteClient(configName);\n    }\n}