using Renci.SshNet;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string host = "your-sftp-server";\n        int port = 22;\n        string username = "your-username";\n        string password = "your-password";\n\n        using (var client = new SftpClient(host, port, username, password))\n        {\n            try\n            {\n                client.Connect();\n                Console.WriteLine("Connected to the SFTP server.");\n\n                // Download a file\n                var remoteFilePath = "/remote/path/to/file.txt";\n                var localFilePath = @"C:\local\path\to\file.txt";\n                using (var fileStream = new FileStream(localFilePath, FileMode.Create))\n                {\n                    client.DownloadFile(remoteFilePath, fileStream);\n                    Console.WriteLine("Downloaded file successfully.");\n                }\n\n                // Upload a file\n                remoteFilePath = "/remote/path/to/uploaded_file.txt";\n                localFilePath = @"C:\local\path\to\file_to_upload.txt";\n                using (var fileStream = new FileStream(localFilePath, FileMode.Open))\n                {\n                    client.UploadFile(fileStream, remoteFilePath);\n                    Console.WriteLine("Uploaded file successfully.");\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Error: {ex.Message}");\n            }\n            finally\n            {\n                if (client.IsConnected)\n                {\n                    client.Disconnect();\n                    Console.WriteLine("Disconnected from the SFTP server.");\n                }\n            }\n        }\n    }\n}