using System;\nusing System.Net;\nusing Microsoft.Silverlight.Controls;\n\npublic class SpeechPlayer\n{\n    public void PlaySpeech(string text)\n    {\n        WebClient webClient = new WebClient();\n        webClient.DownloadStringCompleted += (sender, e) =>\n        {\n            if (!e.Cancelled && e.Error == null)\n            {\n                string audioFilePath = e.Result;\n                // Logic to play the audio file at 'audioFilePath'\n            }\n        };\n        webClient.DownloadStringAsync(new Uri("http://yourserver/TextToSpeech.asmx/ConvertTextToSpeech?text=" + System.Uri.EscapeDataString(text)));\n    }\n}