using System;\nusing System.IO;\nusing System.Net;\n\nclass Program\n{\n    static void Main()\n    {\n        // Example URI\n        string uri = "file:///C:/path/to/xhtml11.dtd"; // File URI\n\n        try\n        {\n            WebRequest request = WebRequest.Create(uri);\n            using (WebResponse response = request.GetResponse())\n            using (Stream stream = response.GetResponseStream())\n            using (StreamReader reader = new StreamReader(stream))\n            {\n                string dtdContent = reader.ReadToEnd();\n                Console.WriteLine("DTD Content:");\n                Console.WriteLine(dtdContent);\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n}