using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string directoryPath = @"c:\mydir";\n        DirectoryInfo directory = new DirectoryInfo(directoryPath);\n        FileInfo[] files = directory.GetFiles("*.txt");\n        \n        foreach (FileInfo file in files)\n        {\n            if (file.Exists)\n            {\n                Console.WriteLine(file.FullName);\n            }\n        }\n    }\n}