var sessionFactory = Fluently.Configure()\n    .Database(SQLiteConfiguration.Standard.ConnectionString(c => c.FromConnectionStringWithKey("SQLite")))\n    .Mappings(m => m.FluentMappings.AddFromAssemblyOf<Customer>())\n    .ExposeConfiguration(config =>\n    {\n        config.SetProperty(NHibernate.Cfg.Environment.DefaultSchema, ""); // No schema for SQLite\n    })\n    .BuildSessionFactory();