public partial class YourForm : Form\n{\n    public YourForm()\n    {\n        InitializeComponent();\n        // Wire up the custom button click event\n        yourBigButton.Click += YourBigButtonClick;\n    }\n\n    private void YourBigButtonClick(object sender, EventArgs e)\n    {\n        bindingNavigator1.BindingSource.AddNew();\n    }\n\n    protected override bool ProcessCmdKey(ref Message msg, Keys keyData)\n    {\n        if (keyData == (Keys.Alt | Keys.A))\n        {\n            yourBigButton.PerformClick(); // Simulate the click of Big Button\n            return true;\n        }\n        return base.ProcessCmdKey(ref msg, keyData);\n    }\n}