using System;\nusing System.Web.Security;\n\npublic class UserManager\n{\n    public static void CreateUserAndAddToRole(string username, string password, string email, string roleName)\n    {\n        try\n        {\n            // Create the user\n            MembershipUser newUser = Membership.CreateUser(username, password, email);\n            \n            if (newUser != null)\n            {\n                // Add the user to a role\n                Roles.AddUserToRole(newUser.UserName, roleName);\n                \n                Console.WriteLine($"User '{username}' created and added to role '{roleName}'.");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n\n    public static void Main()\n    {\n        // Example usage\n        CreateUserAndAddToRole("newuser", "P@ssW0rd!", "email@example.com", "NewRoleName");\n    }\n}