using System;\nusing System.Text.RegularExpressions;\nusing System.Xml;\n\npublic static class XmlSanitizer\n{\n    private static readonly Regex IllegalXmlChars = new Regex(\n        "[\x00-\x08\x0B\x0C\x0E-\x1F\uD800-\uDFFF\uFFFE\uFFFF]",\n        RegexOptions.Compiled);\n\n    public static string Sanitize(string input)\n    {\n        if (input == null) return null;\n        // Replace illegal characters with an empty string\n        return IllegalXmlChars.Replace(input, "");\n    }\n}\n\n// Usage example:\nstring userInput = "Some text with \x01 illegal chars";\nstring sanitizedInput = XmlSanitizer.Sanitize(userInput);\n\n// Now you can safely convert 'sanitizedInput' to XML or use it in your SqlDataCommand.