using System;\nusing System.Web;\n\npublic class RenderControlHandler : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        // Create a new instance of your UserControl\n        MyControl control = (MyControl)context.Handler.LoadControl("~/Path/To/MyControl.ascx");\n        \n        // Add any necessary initialization here, e.g., setting properties\n        control.SomeProperty = "SomeValue";\n\n        // Render the control to a string writer and write it back\n        using (System.IO.StringWriter stringWriter = new System.IO.StringWriter())\n        {\n            using (System.Web.UI.HtmlTextWriter writer = new System.Web.UI.HtmlTextWriter(stringWriter))\n            {\n                Page page = new Page();\n                page.Controls.Add(control);\n                context.Server.Execute(page, writer, true);\n\n                // Write the resulting HTML to the response\n                context.Response.Write(stringWriter.ToString());\n            }\n        }\n\n        // Set the content type of the response to text/html\n        context.Response.ContentType = "text/html";\n    }\n\n    public bool IsReusable => false;\n}