using System;\nusing System.Diagnostics;\n\npublic void ExecuteExternalTool(string filePath, string userInput)\n{\n    // Validate user input if necessary before adding to arguments\n    // For demonstration, assume it's validated\n\n    Process process = new Process();\n    ProcessStartInfo startInfo = new ProcessStartInfo();\n\n    startInfo.FileName = "your-external-tool.exe"; // Replace with your tool name\n    startInfo.Arguments.Add(filePath); // Add each argument separately to prevent injection\n    startInfo.Arguments.Add(userInput);\n    startInfo.UseShellExecute = false; // Do not use the shell, which is safer\n\n    process.StartInfo = startInfo;\n    process.Start();\n}