using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string strFileName = "C:\\MyPicture.jpeg";\n\n        // Use 'using' statement to ensure proper disposal of the FileStream\n        using (FileStream inFile = new FileStream(strFileName, FileMode.Open, FileAccess.Read))\n        {\n            byte[] binaryData = new byte[inFile.Length];\n            int bytesRead = inFile.Read(binaryData, 0, (int)inFile.Length);\n        } // inFile is closed here automatically\n    }\n}