// Form_Load :\nThread t = new Thread(displaySentences);\nt.Start();\n\n// display sentences:\nvoid displaySentences()\n{\n    while (true)\n    {\n        if (i >= 5)\n            i = 0;\n\n        // Use Invoke to update the UI from a non-UI thread\n        label4.Invoke((MethodInvoker)delegate\n        {\n            label4.Text = textarray[i];\n        });\n        \n        i++;\n        Thread.Sleep(2000); // Adding sleep to avoid rapid changes and CPU usage\n    }\n}