public class LocalizationController : Controller\n{\n    public JsonResult GetLocalizedScript()\n    {\n        var localizedStrings = new Dictionary<string, string>();\n\n        // Assuming ResourceManager is setup correctly for different cultures\n        ResourceSet resourceSet = Resources.ResourceManager.GetResourceSet(CultureInfo.CurrentCulture, true, true);\n\n        foreach (DictionaryEntry entry in resourceSet)\n        {\n            localizedStrings.Add(entry.Key.ToString(), entry.Value.ToString());\n        }\n\n        return Json(localizedStrings, JsonRequestBehavior.AllowGet);\n    }\n}